% reconocer canción, al darle un fragmento de letra.

reconocer(LstFragmento,Letra,Titulo,Artista,Genero):-
    findall(X,cancion(Titulo,Artista,Genero,X),LstLetras),
       busca(LstFragmento, Letra, Titulo, Artista,Genero, LstLetras).

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

% funciones auxiliares.

busca(_,_,_,_,_,[]):-
    write("------------------------------------------------------------\n"),
       write("CANCION NO ENCONTRADA").

busca(Letra,X,Titulo,Artista,Genero,[X|_]):- mismoConjunto(Letra,X),
    write("------------------------------------------------------------\n"),
     cancion(Titulo,Artista,Genero,X),
     artistasDeLosGeneros(Genero,LstAux), write("tal vez te interese:"),
     eliminaRepetidos(LstAux,LstAux2), removeElement(LstAux2,Artista,LstArtistas),write(LstArtistas),
    write("\n------------------------------------------------------------\n"),
    write("\nCANCIÓN ENCONTRADA:\n\n").

busca(Letra,Aux,Titulo,Artista,Genero,[_|LstLetras]):-
    busca(Letra, Aux,Titulo,Artista,Genero,LstLetras).

mismoConjunto(List1,List2) :- count(List1,Aux1), count(List2,Aux2),
    Aux1=<Aux2, revisa(List1,List2,[]).

mismoConjunto(List1,List2):- revisa(List2,List1,[]).

revisa([],_,_).

revisa([X|Y],[X|Ys],Aux) :- concat([X],Aux,Raux),
    revisa(Y,Ys,Raux).

revisa([X|Y],[_|Ys],Aux) :- concat(Aux,[X|Y],Raux),
    revisa(Raux,Ys,[]).

concat([],Lista,Lista).
concat([X|Y],Lista,[X|Result]) :- concatenate(Y,Lista,Result).

count([],0).
count([_|Y],Count):- count(Y,Aux), Count is Aux+1.

concatenate([],Lista,Lista).
concatenate([X|Y],Lista,[X|Result]) :- concatenate(Y,Lista,Result).

removeElement([],_,[]).
removeElement([X|Y],X,Result) :- removeElement(Y,X,Result).
removeElement([X|Y],A,[X|Result]) :- removeElement(Y,A,Result).


eliminaRepetidos([],[]).
eliminaRepetidos([X|Y],Result) :- search(X,Y),eliminaRepetidos(Y,Result).
eliminaRepetidos([X|Y],[X|Result]) :- eliminaRepetidos(Y,Result).

search(Head,[Head|_]).
search(Num, [_|Rest]):- search(Num,Rest).

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

% mostrar todos los artistas de los géneros especificados en una lista.

artistasDeLosGeneros([],[]).
artistasDeLosGeneros([X|Y],LstArtistas):-
    findall(Artista,artistaDelGenero(X,Artista),Lst),
       artistasDeLosGeneros(Y,LstAux), concat(Lst,LstAux,LstAux2),
       eliminaRepetidos(LstAux2, LstArtistas).

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

% mostrar el listado completo de todos los artistas registrados.

mostrarTodosLosArtistas():- findall(Artista, cancion(_,Artista,_,_),LstAux),
    eliminaRepetidos(LstAux,LstArtistas),imprimirArtistas(LstArtistas).

imprimirArtistas([]).
imprimirArtistas([X|LstArtistas]):-
    write("["), write(X), write("]"), cancion(_,X,Generos,_), write(", "), write("\n"), write(Generos),write(", "),
    write("\n"), findall(Cancion, cancion(Cancion,X,_,_),LstCanciones),
    write(LstCanciones),
    write("\n------------------------------------------------------------\n"),
    imprimirArtistas(LstArtistas).

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

% mostrar artistas del genero especificado.

artistaDelGenero(Genero,Artista):-
    cancion(_,Artista,LstGeneros,_), mismoConjunto([Genero],LstGeneros).

